<!DOCTYPE html>
<html lang="fr">
<head>
    <meta charset="UTF-8">
    <script src="https://unpkg.com/boxicons@2.1.4/dist/boxicons.js"></script>
    <script src="../scripts/jquery-3.7.1.min.js"></script>
    <title>Name</title>
    <link rel="stylesheet" href="../navbar2/navabar2.css">
</head>
<body>
    <nav>
        <div>        
            <box-icon name="world" color="#fff" size="30px"></box-icon>
            <span>Mondial World</span>
        </div>
        <ul>
            <li>About</li>
            <li>Services</li>
            <li>Testimonials</li>
        </ul> 
        <div> 
            <box-icon name="github" type="logo" color="#fff" size="30px"></box-icon>
            <box-icon id='menu' name="menu" color="#fff" size="30px"></box-icon>
        </div>
    </nav>
    <script> /* On utilise Jquery : jquery c'est du Js simplifi√© */
        $('#menu').click(function() {
            if ($('nav ul').is(':visible')) {
                $('nav ul').animate({
                    opacity: 0,
                }, 500, function() {
                    $('nav ul').hide() /* Tu caches */
                })
            } else {
                $('nav ul').show() /* show c'est montrer */
                $('nav ul').animate({ /* animer */
                    opacity: 1,
                    left: '50px'
                }, 500)

                $('nav').css({
                    'background-color': '#000D1A',
                    'bottom': 0,
                })
                
            }
        })
                
    </script>
